const Header = (props) => {
  return (
    <div>
        <h1>{props.course}</h1>
    </div>
  )
}
const Content = (props) => {
  console.log(props[0])
  return (
    <div>
      <Part contentPart={props[0].name} excercisePart={props[0].exercises} />
      <Part contentPart={props.parts[1].name} excercisePart={props.parts[1].exercises} />
      <Part contentPart={props.parts[2].name} excercisePart={props.parts[2].exercises} />
    </div>
  )
}
const Part = (props) => {
  return (
    <div>
       <p>{props.contentPart} {props.excercisePart}</p>
    </div>
  )
}
const Total = (props) => {
  return (
    <div>
        <p>Number of exercises {props.parts[0].exercises+ props.parts[1].exercises+ props.parts[2].exercises}</p>
    </div>
  )
}

const App = () => {
  const course = {
    name: 'Half Stack application development',
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10
      },
      {
        name: 'Using props to pass data',
        exercises: 7
      },
      {
        name: 'State of a component',
        exercises: 14
      }
    ]
  }
  const parts = [
  {
    name: 'Fundamentals of React',
    exercises: 10
  },
  {
    name: 'Using props to pass data',
    exercises: 7
  },
  {
    name: 'State of a component',
    exercises: 14
  },
]

  return (
    <div>
        <Header course={course.name}/>
        <Content parts={course.parts}/>
        <Total parts={parts.parts}/>
    </div>
  )
}

export default App
