const Header = (props) => {
  return (
    <div>
        <h1>{props.course}</h1>
    </div>
  )
}
const Content = (props) => {
  console.log(props)
  return (
    <div>
      <p>{props.part1x.exercises}</p>
      <Part contentPart={props.part1x.name} excercisePart={props.part1x.excercises} />
      <Part contentPart={props.part2x.name} excercisePart={props.part2x.excercises} />
      <Part contentPart={props.part3x.name} excercisePart={props.part3x.excercises} />
    </div>
  )
}
const Part = (props) => {
  return (
    <div>
       <p>{props.contentPart} {props.excercisePart}</p>
    </div>
  )
}
const Total = (props) => {
  return (
    <div>
        <p>Number of exercises {props.excercises[0]+ props.excercises[1]+ props.excercises[2]}</p>
    </div>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to pass data'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14
  const partArray = [part1, part2, part3]
  const excerciseArray= [exercises1, exercises2, exercises3]

  const part1x = {
    name: 'Fundamentals of React',
    exercises: 10
  }
  const part2x = {
    name: 'Using props to pass data',
    exercises: 7
  }
  const part3x = {
    name: 'State of a component',
    exercises: 14
  }

  return (
    <div>
        <Header course={course}/>
        <Content part1x={part1x} part2x={part2x} part3x={part3x}/>
        <Total excercises={excerciseArray}/>
    </div>
  )
}

export default App
